list.definition:
SET "user.email" = '${__user.email}';
SET vessel.id = '${__user.login}';
SELECT
        v.name AS __text,
        m.vessel_id AS __value
   FROM auth.vessels v
   JOIN api.metadata m ON v.owner_email = '${__user.email}' and m.vessel_id = v.vessel_id;

